    private void ImpostaBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ImpostaBActionPerformed
        String ai = this.ainput.getText();
        double a = Double.parseDouble(ai);
        String bi = this.binput.getText();
        double b = Double.parseDouble(bi);
        String ci = this.cinput.getText();
        double c = Double.parseDouble(ci);
        Retta r;
        r = new Retta(a, b, c);
        String tiporetta = r.getTipo();
        String equazione = r.stampa();
        if((a!=0)&&(b!=0)){
            this.equazionestring.setText(equazione);
            this.tiporettastring.setText("Tipo di retta: " + tiporetta);
        }else{ 
            this.equazionestring.setText("La retta non esiste");
            this.tiporettastring.setText("La retta non esiste");
        }
    }//GEN-LAST:event_ImpostaBActionPerformed

    private void VerificaBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VerificaBActionPerformed
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               Retta r;
        String ai = this.ainput.getText();
        double a = Double.parseDouble(ai);
        String bi = this.binput.getText();
        double b = Double.parseDouble(bi);
        String ci = this.cinput.getText();
        double c = Double.parseDouble(ci);
        r = new Retta(a, b, c);
        boolean t;
        String p1i = this.p1input.getText();
        double p1 = Double.parseDouble(p1i);
        String p2i = this.p2input.getText();
        double p2 = Double.parseDouble(p2i);
        t = r.appartiene(p1, p2);
        if((a!=0)&&(b!=0)){
            if (t == true) {
                this.stampafine.setText("Il punto APPARTIENE alla retta");
            } else {
                this.stampaappartenenza.setText("Il punto NON appartiene alla retta");
        }
        }else{ 
            this.stampafine.setText("La retta non esiste");
            this.stampaappartenenza.setText("La retta non esiste");
        }
    }//GEN-LAST:event_VerificaBActionPerformed

